import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages([...messages, userMessage]);
    setInput('');

    // In the sendMessage function
    try {
      // Add loading message
      setMessages(prev => [...prev, { text: '...', sender: 'bot', loading: true }]);
      
      // Call backend API with the correct URL
      const response = await axios.post('http://localhost:3000/api/chat', { message: input });
      
      // Remove loading message and add actual response
      setMessages(prev => {
        const filtered = prev.filter(msg => !msg.loading);
        return [...filtered, { text: response.data.reply, sender: 'bot' }];
      });
    } catch (error) {
      console.error('Error sending message:', error);
      
      // Remove loading message and add error message
      setMessages(prev => {
        const filtered = prev.filter(msg => !msg.loading);
        return [...filtered, { 
          text: 'Sorry, I encountered an error. Please try again later.', 
          sender: 'bot', 
          error: true 
        }];
      });
    }
  };

  return (
    <div className="chatbot-container">
      <button 
        className="chatbot-toggle"
        onClick={() => setIsOpen(!isOpen)}
      >
        {isOpen ? 'Close Chat' : 'Chat with us'}
      </button>
      
      {isOpen && (
        <div className="chatbot-window">
          <div className="chatbot-header">
            <h3>Iris Essentials Assistant</h3>
            <div className="header-buttons">
              <button 
                className="settings-button" 
                onClick={() => setShowSettings(!showSettings)}
                title="Settings"
              >
                ‚öôÔ∏è
              </button>
              <button onClick={() => setIsOpen(false)}>√ó</button>
            </div>
          </div>
          
          {showSettings ? (
            <div className="chatbot-settings">
              <h4>Chat Settings</h4>
              <div className="settings-option">
                <label>
                  <input type="checkbox" defaultChecked={true} />
                  Enable notifications
                </label>
              </div>
              <div className="settings-option">
                <label>
                  <input type="checkbox" defaultChecked={true} />
                  Save chat history
                </label>
              </div>
              <div className="settings-option">
                <button 
                  className="clear-history"
                  onClick={() => {
                    setMessages([]);
                    setShowSettings(false);
                  }}
                >
                  Clear Chat History
                </button>
              </div>
              <div className="settings-option">
                <button 
                  className="back-to-chat"
                  onClick={() => setShowSettings(false)}
                >
                  Back to Chat
                </button>
              </div>
            </div>
          ) : (
            <>
              <div className="chat-messages">
                {messages.length === 0 ? (
                  <div className="welcome-message">
                    <p>üëã Hi there! How can I help you today?</p>
                  </div>
                ) : (
                  messages.map((message, index) => (
                    <div 
                      key={index} 
                      className={`message ${message.sender} ${message.error ? 'error' : ''}`}
                    >
                      {message.text}
                    </div>
                  ))
                )}
                <div ref={messagesEndRef} />
              </div>
              <form onSubmit={sendMessage} className="chat-input-form">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="Type your message..."
                />
                <button type="submit">Send</button>
              </form>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default Chatbot;
